mapper.update:
update img i
set i.is_delete = true
where i.id in (

    #{item.id}

)

mapper.select:
select * from img i left join user u on u.id = i.user_id
i.user_id = #{userId}
and !i.is_delete
order by i.id desc limit #{startPage}, #{pageSize}

select *
from img i
where i.field like concat('%', #{field}, '%')
  and i.user_id = #{userId}
  and i.id >= (RAND() * (select MAX(s.id) from img s))
limit #{count}
